import { Component } from "react";

class Ex3_2_ColorsCheckbox2_0Comp extends Component
{
    constructor()
    {
        super()
        this.state = {red:false, green:false, blue:false}
    }

    handleChange = (e)=>
    {
        
        // option 2
        //const {name} = e.target; // const name = e.target.name

        // this.state.blue = !this.state.blue - NEVER USE THIS METHOD - ONLY USE SetState
        // this.state['blue'] = !this.state['blue'] - NEVER USE THIS METHOD - ONLY USE SetState
        // this.state[name] = !this.state[name] - NEVER USE THIS METHOD - ONLY USE SetState
        // this.setState({[name]: !this.state[name]}) - 

        //this.setState((prevState) => ({[name]: !prevState[name]})) // computed property name

        // Option 3
        const {name, checked} = e.target // const name = e.target.name | const checked = e.target.checked
        this.setState({[name]:checked})
        
        // console.log(name)
        // console.log(checked)


        
    }

    render()
    {
        const redItem = this.state.red? <li>Red</li>:null
        const greenItem = this.state.green?<li>Green</li>:null
        const blueItem = this.state.blue?<li>Blue</li>:null

        return (<div>

                <h3>Ex3_2</h3>
                <input type="checkbox" name="red" onChange={this.handleChange}/> Red <br/>
                <input type="checkbox" name="green" onChange={this.handleChange}/> Green <br/>
                <input type="checkbox" name="blue" onChange={this.handleChange}/> Blue<br/>
                <ul>
                    {redItem}
                    {greenItem}
                    {blueItem}
                </ul>

        </div>)
    }

}

export default Ex3_2_ColorsCheckbox2_0Comp
