import { Component } from "react";

class StateComp extends Component
{
    constructor()
    {
        super()
        this.state = {name:'',counter:null}
    }

    increment =() =>
    {
        let newCount = this.state.counter;
        newCount++;
        //this.state.counter = newCount -> NEVER!!!!!!!!
        //this.setState({counter:newCount})

        //this.setState({counter:(this.state.counter) + 1 }) // do not use this.state in setState
        this.setState((prevState) => ({counter:prevState.counter+1}))   
    }

    handleInputChange = (e) =>
    {
        let name = e.target.value;
        this.setState({name})

    }
    showName = () =>
    {
        alert(this.state.name)
    }
    render()
    {
        return(<div style={{border:"solid purple 2px", backgroundColor:"lightgrey"}}>
            <h2>State Comp</h2>

            Counter Value: {this.state.counter}<br/>
            <button onClick={this.increment}>+</button> <br/>
            <hr/>

            <input type="text" onChange={this.handleInputChange} value={this.state.name}/><br/>
            name: {this.state.name} <br/>
            <button onClick={this.showName}>Show Name</button> <br/>
        </div>)
    }
}
export default StateComp;
