import axios from "axios";

const usersURL = 'http://localhost:9000/users'

//get all users:
const getAllUsers = async () =>(await axios.get(usersURL)).data;

//Geting the todos of specific user by its ID:
const getUserById = async (id) => (await axios.get(`${users}/${id}`)).data;

export {getAllUsers,getUserById}
