import { Component } from "react";
import Demo10_CB_Func2_Child from "./Demo10_CB_Func2_Child";

class Demo10_CB_Func_Parent extends Component
{
    constructor()
    {
        super()
        this.state = {dataToChild:'Hello from parent', dataFromChild:''};
    }

    getDataFromChild = (childValue) => {
        this.setState({data:childValue})
    }

    render()
    {
        return (<div style={{border:"2px solid Green", backgroundColor:"lightgreen"}}>

                <h3>Demo10 CB-Func2</h3>
                Data from Child: {this.state.dataFromChild} <br/>

                <input type="button" value="Change Data" onClick={()=> this.setState({dataToChild:'Hello Again'})}/>

                <Demo10_CB_Func2_Child dataToChild={this.state.dataToChild} callback={childValue=> this.setState({dataFromChild:childValue})} />

        </div>)
    }
}

export default Demo10_CB_Func_Parent;
