import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import { getMovieByID } from '../DALs/MoviesDAL'
import { checkIfHasSubscription, getSubscriptionsByMemberId } from '../Utils/Subscriptions_Utils'
import MovieSubscribeComp from './MovieSubscribeComponent'

export default function MoviesWatchedComp(props) {
    //states:
    const [moviesArray, setMoviesA] = useState()
    const [boolean, setBoolean] = useState(false)
    const [showAddMovie, setshowAddMovie] = useState(false)

    //Chcecking if the member has a subscription:
    useEffect(async()=>{
        if(await checkIfHasSubscription(props.id))//if member has subscriptions
        {
            const subscriptions = await getSubscriptionsByMemberId(props.id)//subscriptions of member
            let arr = []//array of movies names and date
            for(let movie of subscriptions.movies)//for each movie of subscriptions DB
            {
                let obj = await getMovieByID(movie.movieId)//get movie from DB
                let newObj = {//create movie object
                    name: obj.name,
                    date: movie.date
                }
                arr.push(newObj)//add to array
            }
            setMovies(arr)
        }
        else
        {
            if(props.refresh)
            {
                props.cb()
            }
            setMovies([])
        }
    },[boolean,props.refresh,props.id])

    const refresh = () => {
        setshowAddMovie(!showAddMovie)
        setBoolean(!boolean)
        props.cb()
    }

    let moviesList = ""
    if(movies !== undefined){
        moviesList = movies.map((movie,index)=><li key = {index}><Link to={`/mainPage/movie/${movie.name}`}>{movie.name}</Link>{movie.date}</li>)
    }
    return(<div>
        <h4>Movies Watched:</h4>
        <button onClick={()=>setshowAddMovie(showAddMovie)}>Subscribe to new movie </button>
        <MovieSubscribeComp id={props.id} cb={refresh} show = {showAddMovie}/><br/>
        <ul>{moviesList}</ul>
    </div>)
}
