import { Component } from "react";
import Demo9_CB_Func_Child from "./Demo9_CB_Func_Child";

class Demo9_CB_Func_Parent extends Component
{
    constructor()
    {
        super()
        this.state = {data:''};
    }

    getDataFromChild = (childValue) => {
        this.setState({data:childValue})
    }

    render()
    {
        return (<div style={{border:"2px solid Yellow", backgroundColor:"lightgrey"}}>

                <h3>Demo9 CB-Func</h3>
                Data from Child: {this.state.data}
                <Demo9_CB_Func_Child callback={this.getDataFromChild}/>

        </div>)
    }
}

export default Demo9_CB_Func_Parent;
