import { Component } from "react";
import { getUserByID} from "../DALs/DAL_USERS";
import { getUserTasks, uncompletedTask } from "../DALs/DAL_TODOS";
import Tasks from "./Tasks";
import "./styleDiv.css"



const usersURL = 'https://jsonplaceholder.typicode.com/users'

export default class Users extends Component
{
    constructor(props)
    {
        super(props)
        this.state = {name:"", email:"", tasks:[], address:[], completed:"", isShowingTask:false, mouseInside:false, isVisible:false}
    }

 

    async componentDidMount()
    {
        const user = await getUserByID(usersURL, this.props.id)
        this.setState({name:user.data.name, email:user.data.email, address:user.data.address})
        
        const taskStatus = await uncompletedTask(this.props.id)
         if(taskStatus === true){
            this.setState({completed:true})        
        }
        if(taskStatus ===false){
            this.setState({completed:false})
        }
        
    }

    
     
    showTasks = async()=>
    {

        if (!this.state.isShowingTask)
        {
            const tasks = await getUserTasks(this.props.id)
            this.setState(prevState => ({tasks, isShowingTask:!prevState.isShowingTask}))
            this.setState({isVisible:true})
        }
        else
        {
            this.setState(prevState => ({tasks:[], isShowingTask:!prevState.isShowingTask}))
            this.setState({isVisible:false})

        }
    }


    mouseEnter = () => {
        this.setState({ mouseInside: true });
    }

    mouseLeave = () => {
        this.setState({ mouseInside: true });
    }

    mouseClick = () => {
        this.setState({ mouseInside: false });
    }


    render()
    { 
        let colorStyle;
        let colorBack;
        
        colorStyle = this.state.completed?'greenStyle':'redStyle'
        colorBack = this.state.isVisible?'orangeStyle':'whiteStyle'

        const tasksRep = this.state.tasks.map((task,index) => {
        return (<div key={index}><Tasks todo={task}/><br/></div>)})
        
        
        const adressUser = <div style={{ border: '2px solid violet', borderRadius: '12px', padding: '5px', backgroundColor: 'pink' }} onClick={this.mouseClick}>
            Street: <input type='text' value={this.state.address.street} /><br />
            City: <input type='text' value={this.state.address.city} /><br />
            Zip Code: <input type='text' value={this.state.address.zipcode} /><br />
        </div>


        const showingTaskWindow = <div>{this.state.isShowingTask ? <div style={{border: '2px solid black', borderRadius: '12px', padding: '5px', width: '300px', height: '300px', overflow: 'scroll', margin: 'left', position: 'absolute', left: '700px'
        }}>{tasksRep}</div> : ''}</div>



        
        return (
        <div> {showingTaskWindow}
            <div className ={colorStyle} style ={{colorBack}}>
            <div  onClick={this.showTasks}><h4>ID: {this.props.id}</h4></div>

            Name:<input type = "text" value ={this.state.name}/><br/>
            Email:<input type = "email" value ={this.state.email}/><br/>
                    <div>
                        {this.state.mouseInside ? adressUser : null}
                    </div>
            <button onMouseEnter={this.mouseEnter} onMouseLeave={this.mouseLeave}>Other Data</button>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
            <button>Update</button>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
            <button>Delete</button><br/>

            </div>
        </div>)
    }
}
