function Ex9_reducer(state={users:[]},action)
{
    switch(action.type)
    {
        case "ADD":
            return {...state,users:[...state.users,action.payload]}

        case "UPDATE":
            const updatedUser = action.payload
            const updUsers = [...state.users]
            const updIndex = updUsers.findIndex(user=>user.id==updatedUser.id)
            
            // Checking whether there is a user to delete
            if (updIndex >=0)
            {
                updUsers[updIndex] = updatedUser;
            }
        
            return {...state,users:[...updUsers]}

        case "DELETE":
            const newUsers = [...state.users]    
            const delIndex = newUsers.findIndex(user=>user.id==action.payload)
            
            // Checking whether there is a user to delete
            if (delIndex >=0)
            {
                newUsers.splice(delIndex,1)
            }
        
            return {...state,users:[...newUsers]}
        default:
            return state;
    }
}

export default Ex9_reducer;
