import React from 'react'
import  { useState } from 'react'
import { useNavigate } from 'react-router';

//2 page: Form(fname and lname inputs, button for going to City page)

export default function FormPage() {

    const navigate = useNavigate();// for transiton to another page


    const [user, setUser] = useState({fname:'',lname:'',isValid:false})
    // Determine stste--->user object(isValid for first name mangetory, in state its false , because state of first name is empty)

    //goNext - function for button(if fname is empty--->alert masage, else going to city page)
    const goNext = (e) =>
    {
        e.preventDefault();
        if (user.isValid)
        {
            navigate(`/city/${user.fname}/${user.lname}`)
        }
        else
        {
            alert('First name is mandatory')
        }  
    }

    return (
        <div style={{background:"yellow", width:"400px", height:"400px"}}>
            <h2>Form</h2>
            <form onSubmit={goNext}>
                    First Name: <input type="text" name="fname" onChange={e=>setUser({...user,fname:e.target.value,isValid:e.target.value?true:false})}/><br/>
                    Last Name: <input type="text" name="lname" onChange={e=>setUser({...user,lname:e.target.value})}/><br/>
                    <button type="submit">Next</button>
                </form>

        </div>
    )
}
