import { Component } from "react";

class PhoneComp extends Component
{
    constructor(props)
    {
        super(props)
    }
    showphones = () =>
    {
        console.log(`Home: ${this.props.phones.homephone}, 
                    Mobile: ${this.props.phones.mobile}, 
                    "Fax: ${this.props.phones.fax}`)

        alert(`Home: ${this.props.phones.homephone}, Mobile: ${this.props.phones.mobile}, "Fax: ${this.props.phones.fax}`)
    }

    render()
    {
        return (<div style={{border:"solid grey 2px", width:"300px"}}>
                <h3>Phones:</h3>
                Home Phone: {this.props.phones.homephone}<br/>
                Mobile: {this.props.phones.mobile}<br/>
                Fax: {this.props.phones.fax}<br/>

                <input style={{color:"blue", backgroundColor:"yellow"}} type="button" value="phones" onClick={this.showphones}/>
        </div>)
    }
}

export default PhoneComp;
