import { render } from "@testing-library/react";
import React, { Component } from "react";
import './Ex1.css'
class Ex1Comp extends Component
{
    constructor(props)
    {
        super(props)
    }

    sayHello =() =>
    {
        alert("Hello")
    }

    render()
    {
        return (<div style={{border:"4px pink solid", backgroundColor:"grey"}}>
            Name: {this.props.name} <br/>
            Age: {this.props.age} <br/>
            City: {this.props.city}<br/>

            <input className= "btn" type="button" value="Say Hello" onClick={this.sayHello}></input>
    
        </div>)
    }
}
export default Ex1Comp;
