import { Component } from "react";

class Ex2_2TotalComp extends Component
{
    constructor()
    {
        super()
        this.state = {sum:0, numbers:[],number:""}
    }

    AddValue = () =>
    {
        this.setState((prevState) => ({sum:prevState.sum+parseInt(prevState.number), numbers:[...prevState.numbers,prevState.number + ', ']}))
    }

    render()
    {
        return(<div>
            <input type="number" name="number" onChange={(e)=>this.setState({number:e.target.value})}/><br/>
            <input type="button" value="Add" onClick={this.AddValue}/><br/>
            Sum: {this.state.sum} <br/>
            Numbers: {this.state.numbers} <br/>


        </div>)
    }
}

export default Ex2_2TotalComp;
