import { Component } from "react";
import Demo7ChildComp from "./Demo7_ChildComp";

class Demo7_UsersComp extends Component
{
    constructor()
    {
        super()
        this.state = {
            users:['Avi','Dana','Moses','Vered']
        }
    }

    render()
    {
        const users = this.state.users.map((user,index)=>{
            return (<div><Demo7ChildComp key={index} name={user}/><br/></div>)})
            
        return (<div style={{border:"4px solid blue", backgroundColor:"ivory"}}>
                    <h3>Demo 7</h3>
                    {users}
                </div>)
    }

}
export default Demo7_UsersComp;
